def unique(s: str):
    s = list(s)
    return len(s) == len(set(s))


def packet_index(code: str, distinct_amount: int):
    i = 0
    while i < len(code):
        if unique(code[i:i+distinct_amount]):
            return i + distinct_amount
        i += 1

    raise ValueError('provided code has no packet index')

